// CÓDIGO PARA REQUISIÇÃO SEMANAL
console.log('🚀 INICIANDO PROCESSAMENTO SEMANAL...');

// Função para obter chave da semana atual
function getWeekKey() {
  const now = new Date();
  const startOfWeek = new Date(now.setDate(now.getDate() - now.getDay()));
  const year = startOfWeek.getFullYear();
  const weekNumber = Math.ceil((startOfWeek.getDate() + startOfWeek.getDay()) / 7);
  return `${year}-W${weekNumber.toString().padStart(2, '0')}`;
}

// Extrair signo da requisição
const { sign } = msg.payload;

console.log('📥 Payload recebido:', msg.payload);
console.log('📥 Signo extraído:', sign);

if (!sign) {
  console.error('❌ Signo não especificado');
  msg.payload = {
    success: false,
    error: "Signo não especificado",
    message: "Parâmetro 'sign' é obrigatório"
  };
  return msg;
}

// Gerar URL completa para horóscopo semanal
const semana = getWeekKey();
const urlCompleta = `https://firestore.googleapis.com/v1/projects/tarot-universo-catia/databases/(default)/documents/horoscopos_semanais/${semana}/signos/${sign}`;

console.log('🔍 DEBUG: URL completa gerada (SEMANAL):');
console.log('  ', urlCompleta);

// IMPORTANTE: Definir msg.url com a URL completa
msg.url = urlCompleta;
msg.method = 'GET';
msg.headers = {
  'Content-Type': 'application/json'
};

// Armazenar informações adicionais
msg.isSemanal = true;
msg.signo = sign;
msg.semana = semana;

console.log('✅ DEBUG: Configuração concluída (SEMANAL)');
console.log('  - msg.url:', msg.url);
console.log('  - msg.method:', msg.method);
console.log('  - msg.headers:', msg.headers);

return msg;

// CÓDIGO PARA PROCESSAR RESPOSTA DIÁRIA DO FIREBASE
console.log('🔄 PROCESSANDO RESPOSTA DIÁRIA DO FIREBASE...');

// Verificar se há resposta do HTTP Request
if (!msg.payload) {
  console.error('❌ Nenhuma resposta recebida');
  msg.payload = {
    success: false,
    error: "Nenhuma resposta do Firebase",
    message: "Dados não encontrados"
  };
  return msg;
}

console.log('📥 Resposta recebida:', typeof msg.payload);

// Verificar se é uma resposta de erro
if (msg.payload.error || msg.payload.success === false) {
  console.error('❌ Erro na resposta:', msg.payload);
  return msg;
}

// Verificar se é uma resposta do Firebase
if (msg.payload.fields) {
  console.log('✅ Resposta do Firebase detectada (DIÁRIO)');

  try {
    // Processar dados do Firebase
    const dados = msg.payload.fields;

    // Extrair dados do destaque
    const destaque = dados.destaque?.mapValue?.fields || {};
    const destaqueProcessado = {
      titulo: destaque.titulo?.stringValue || "Horóscopo Diário",
      mensagem: destaque.mensagem?.stringValue || "Mensagem do dia",
      tema: destaque.tema?.stringValue || "Geral",
      cor: destaque.cor?.stringValue || "#8B5CF6"
    };

    // Extrair dados do dia específico (segunda-feira como exemplo)
    const segunda = dados.segunda?.mapValue?.fields || {};
    const diaProcessado = {
      tema: segunda.tema?.stringValue || "Geral",
      trecho: segunda.trecho?.stringValue || "Mensagem do dia",
      cor: segunda.cor?.stringValue || "#8B5CF6",
      icone: segunda.icone?.stringValue || "FaStar"
    };

    // Montar resposta final
    const resposta = {
      success: true,
      data: {
        destaque: destaqueProcessado,
        dia: diaProcessado,
        signo: msg.signo || "aries",
        semana: msg.semana || "2025-W04"
      }
    };

    console.log('✅ Dados diários processados com sucesso:', resposta);
    msg.payload = resposta;

  } catch (error) {
    console.error('❌ Erro ao processar dados diários:', error);
    msg.payload = {
      success: false,
      error: "Erro ao processar dados do Firebase",
      message: error.message
    };
  }

} else {
  console.error('❌ Formato de resposta inválido:', msg.payload);
  msg.payload = {
    success: false,
    error: "Formato de resposta inválido",
    message: "Dados não estão no formato esperado"
  };
}

return msg;

// CÓDIGO PARA PROCESSAR RESPOSTA SEMANAL DO FIREBASE
console.log('🔄 PROCESSANDO RESPOSTA SEMANAL DO FIREBASE...');

// Verificar se há resposta do HTTP Request
if (!msg.payload) {
  console.error('❌ Nenhuma resposta recebida');
  msg.payload = {
    success: false,
    error: "Nenhuma resposta do Firebase",
    message: "Dados não encontrados"
  };
  return msg;
